import React, { useState } from 'react';
import styles from './Chat.module.css'; // Update your CSS styles

const Chat = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [groupName, setGroupName] = useState('');
  const [selectedColor, setSelectedColor] = useState(null);
  const colors = ['red', 'green', 'blue']; // Add your color options

  const openPopup = () => {
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  const handleCreateGroup = () => {
    // Implement the logic for creating a group here
  };

  return (
    <div className={styles.chatApp}>
      <div className="group-container">
        <h1>Pocket Notes</h1>
        <button className="create-group-btn" onClick={() => openPopup()}>
          <img src={createGroupIcon} alt="Create Group" />
          Create Group
        </button>
      </div>

      <div className={isPopupOpen ? styles.popup : styles.rightSideContent}>
        {/* Cross icon for closing the popup */}
        {isPopupOpen && (
          <span className={styles.closeIcon} onClick={() => closePopup()}>
            &#10006;
          </span>
        )}
        {isPopupOpen ? (
          <>
            <h2>Create New Notes Group</h2>
            <p>Group Name</p>
            <input
              type="text"
              placeholder="Enter your group name...."
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
            />
            <div className="sidebar">
              <p>Choose color</p>
              <ul className="style.sidebar_list">
                {colors.map((item, index) => (
                  <li
                    key={index}
                    className={`sidebar_list_item ${
                      selectedColor === item ? 'selected' : ''
                    }`}
                    style={{ backgroundColor: item }}
                    onClick={() => setSelectedColor(item)}
                  />
                ))}
              </ul>
            </div>
            <button className={styles.createBtn} onClick={handleCreateGroup}>
              Create
            </button>
          </>
        ) : (
          <>
            <img src={bgImage} alt={bgImage} className={styles.bgImage} />
            <p className={styles.pocketnotes}>Pocket Notes</p>
            <p className={styles.description}>
