import React, { useState } from 'react';
import styles from './ChatApp.module.css'; // Update your CSS styles

// Create a Contact component
function Contact({ contact, onSelectContact }) {
  return (
    <div className={styles.contact} onClick={() => onSelectContact(contact)}>
      {contact.name}
    </div>
  );
}

// Create a Message component
function Message({ message }) {
  return (
    <div className={styles.message}>
      <div className={styles.messageContent}>{message.content}</div>
      <div className={styles.timestamp}>{message.timestamp}</div>
    </div>
  );
}

// Main ChatApp component
export default function ChatApp() {
  const [contacts, setContacts] = useState([
    { id: 1, name: 'Contact 1' },
    { id: 2, name: 'Contact 2' },
    { id: 3, name: 'Contact 3' },
  ]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [messages, setMessages] = useState([]);

  const handleContactSelect = (contact) => {
    setSelectedContact(contact);
    // Load messages for the selected contact (you can fetch messages here).
  };

  const handleSendMessage = (messageText) => {
    // Send the message to the selected contact (you can implement this logic).
    const newMessage = {
      content: messageText,
      timestamp: new Date().toLocaleString(),
    };

    setMessages([...messages, newMessage]);
  };

  return (
    <div className={styles.chatApp}>
      <div className={styles.contacts}>
        {/* Display contacts on the left side */}
        {contacts.map((contact) => (
          <Contact key={contact.id} contact={contact} onSelectContact={handleContactSelect} />
        ))}
      </div>
      <div className={styles.messages}>
        {/* Display messages on the right side */}
        {selectedContact && (
          <div className={styles.messageContainer}>
            {messages.map((message, index) => (
              <Message key={index} message={message} />
            ))}
          </div>
        )}
        {selectedContact && (
          <div className={styles.messageInput}>
            {/* Add an input field and send button for sending messages */}
            {/* You can use your existing input and button code here */}
          </div>
        )}
      </div>
    </div>
  );
}
