function App() {
  return (
    <BrowserRouter>
      <Switch>
        <Route path="/webpage">
          <WebpageView />
        </Route>
        <Route path="/android">
          <AndroidView />
        </Route>
        {/* Add other routes if needed */}
      </Switch>
    </BrowserRouter>
  );
}

export default App;
