import React, { useState } from 'react';
import styles from './RightSideContent.module.css'; // Create a CSS module for this component

const RightSideContent = ({ isPopupOpen, closePopup }) => {
  const [groupName, setGroupName] = useState('');
  const colors = ["#B38BFA", "#FF79F2", "#43E6FC", "#F19576", "#0047FF", "#6691FF"];
  const [listOpen] = useState(false);

  const handleCreateGroup = () => {
    closePopup();
  };

  return (
    <div className={styles.rightside}>
      {isPopupOpen ? (
        <div className={styles.popup}>
          {/* Cross icon for closing the popup */}
          <span className={styles.closeIcon} onClick={() => closePopup()}>
            &#10006;
          </span>
          <h2>Create New Notes Group</h2>
          <p>Group Name</p>
          <input
            type="text"
            placeholder="Enter your group name...."
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
          />
          <div className="sidebar">
            <p>Choose color</p>
            <ul className="style.sidebar_list">
              {colors.map((item, index) => (
                <li
                  key={index}
                  className="sidebar_list_item"
                  style={{ backgroundColor: item }}
                  onClick={() => {/* Handle color selection here */}}
                />
              ))}
            </ul>
          </div>
          <button className={styles.createBtn} onClick={handleCreateGroup}>
            Create
          </button>
        </div>
      ) : (
        <>
          {/* Place your right-side content here */}
        </>
      )}
    </div>
  );
};

export default RightSideContent;
