import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [noteContent, setNoteContent] = useState('');
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Load groups and notes from localStorage on component mount
    const savedGroups = JSON.parse(localStorage.getItem('groups')) || [];
    const savedNotes = JSON.parse(localStorage.getItem('notes')) || [];

    setGroups(savedGroups);
    setNotes(savedNotes);
  }, []);

  useEffect(() => {
    // Save groups and notes to localStorage whenever they change
    localStorage.setItem('groups', JSON.stringify(groups));
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [groups, notes]);

  const getNotesForSelectedGroup = () => {
    return notes.filter((note) => note.group === groups[selectedGroup]);
  };

  const addNote = () => {
    if (noteContent && selectedGroup !== null) {
      const currentTime = new Date().toLocaleString();
      const newNote = {
        content: noteContent,
        group: groups[selectedGroup],
        createdAt: currentTime,
        updatedAt: currentTime,
      };
      setNotes([...notes, newNote]);
      setNoteContent('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addNote();
    }
  };

  const handleGroupChange = (index) => {
    setSelectedGroup(index);
  };

  return (
    <div className="App">
      <h1>Notes App</h1>
      <div className="container">
        <div className="notes-list">
          <ul>
            {groups.map((group, index) => (
              <li key={index}>
                {group}
                <button onClick={() => handleGroupChange(index)}>Select Group</button>
              </li>
            ))}
          </ul>
        </div>
        <div className="notes-content">
          {selectedGroup !== null && (
            <div>
              <input
                type="text"
                placeholder="Enter note content"
                value={noteContent}
                onChange={(e) => setNoteContent(e.target.value)}
                onKeyPress={handleKeyPress}
              />
              <button onClick={addNote}>Add Note</button>
              <span className="enter-icon" onClick={addNote}>
                &#x23CE;
              </span>
              <h2>Notes for {groups[selectedGroup]}</h2>
              {getNotesForSelectedGroup().map((note, index) => (
                <div key={index} className="note">
                  <p>{note.content}</p>
                  <p>Created at: {note.createdAt}</p>
                  <p>Updated at: {note.updatedAt}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
