import React, { useState, useRef } from 'react';
import styles from './Popup.module.css';

const Popup = ({ isPopupOpen, closePopup }) => {
  const [groupName, setGroupName] = useState('');
  const [selectedColor, setSelectedColor] = useState(null);
  const colors = ["#B38BFA", "#FF79F2", "#43E6FC", "#F19576", "#0047FF", "#6691FF"];
  const noteInputRef = useRef(null);
  const [noteText, setNoteText] = useState(''); // State to store note text


  const handleCreateGroup = () => {
    if (groupName && selectedColor) {
      // You can perform any necessary actions when creating a group here
      console.log(`Creating group: ${groupName}`);
      setGroupName('');
      setSelectedColor(null);

      // Save the group to localStorage (use a unique key)
      localStorage.setItem(groupName, JSON.stringify({ name: groupName, color: selectedColor }));

// Clear the note input field
setNoteText('');


      // Focus on the note input field for the next entry
      noteInputRef.current.focus();
    } else {
      // Handle validation or show an error message if necessary
    }
  };

  const handleColorSelection = (color) => {
    setSelectedColor(color);
  };

  return (
    <>
      {isPopupOpen ? (
        <div className={styles.popup}>
          <span className={styles.closeIcon} onClick={closePopup}>
            &#10006;
          </span>
          <h2>Create New Notes Group</h2>
          <p>Group Name</p>
          <input
            type="text"
            placeholder="Enter your group name...."
            value={noteText}
              onChange={(e) => setNoteText(e.target.value)}
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    handleCreateNote();
              }
            }}
            ref={noteInputRef}
          />
          <div className={styles.sidebar}>
            <p>Choose color</p>
            <ul className={styles.sidebarList}>
              {colors.map((color, index) => (
                <li
                  key={index}
                  className={styles.sidebarListItem}
                  style={{ backgroundColor: color }}
                  onClick={() => handleColorSelection(color)}
                />
              ))}
            </ul>
          </div>
          <button className={styles.createBtn} onClick={handleCreateGroup}>
            Create
          </button>
        </div>
      ) : null}
    </>
  );
};

export default Popup;
