import React, { useState } from 'react';
import styles from './ChatApp.module.css'; // Update your CSS styles
// import styles from './ChatApp.module.css';
import lock from '../assets/Vector.png';
import bgImage from '../assets/bgimage.png';
import createGroupIcon from '../assets/+.png';
import popup from './popup';


const ChatApp = () => {
  const [contacts, setContacts] = useState([]); // Replace this with your contact/note data
  const [selectedContact, setSelectedContact] = useState(null);
  const [newMessageText, setNewMessageText] = useState('');
  const [isPopupOpen, setIsPopupOpen] = useState(false); // State to manage the popup

  const openPopup = () => {
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  // Function to create a new message/note
  const createMessage = () => {
    if (selectedContact) {
      // Create a new message/note object
      const newMessage = {
        content: newMessageText,
        sender: 'User', // You can change this to the sender's name
        timestamp: new Date().toLocaleString(), // Current date and time
      };

      // Add the new message/note to the selected contact's notes
      const updatedContacts = contacts.map((contact) => {
        if (contact.id === selectedContact.id) {
          return {
            ...contact,
            notes: [...contact.notes, newMessage],
          };
        }
        return contact;
      });

      setContacts(updatedContacts);
      setNewMessageText('');
    }
  };

  return (
    <div className={styles.chatApp}>
      <div className={styles.contacts}>
        {/* Display contacts/people as notes on the left side */}
        <div className="group-container">
        <h1>Pocket Notes</h1>
        <button className={styles.button} onClick={openPopup}>
          <img src={createGroupIcon} alt="Create Group" />
          Create Notes group
        </button>
      </div>
        {contacts.map((contact) => (         
          <div
            key={contact.id}
            className={`${styles.contact} ${
              selectedContact && selectedContact.id === contact.id
                ? styles.selectedContact
                : ''
            }`}
            onClick={() => setSelectedContact(contact)}
          >
            {contact.name}
          </div>
        ))}
      </div>
      <div className={styles.messages}>
        {/* Display messages/notes on the right side */}
        <img src={bgImage} alt={bgImage} className={styles.bgImage} />
            <p className={styles.pocketnotes}>Pocket Notes</p>
            <p className={styles.description}>
              Send and receive messages without keeping your phone online.
              <br />
              Use Pocket Notes on up to 4 linked devices and 1 mobile phone
            </p>
            <div className={styles.FooterContainer}>
              <img src={lock} alt="lock" />
              <p>end-to-end encrypted</p>
            </div>
        {selectedContact && (
          <div className={styles.messageContainer}>
            {selectedContact.notes.map((note, index) => (
              <div
                key={index}
                className={`${
                  note.sender === 'User' ? styles.sentMessage : styles.receivedMessage
                }`}
              >
                <div className={styles.messageContent}>{note.content}</div>
                <div className={styles.timestamp}>{note.timestamp}</div>
              </div>
            ))}
          </div>
        )}
        {selectedContact && (
          <div className={styles.messageInput}>
            <input
              type="text"
              placeholder="Type your message here..."
              value={newMessageText}
              onChange={(e) => setNewMessageText(e.target.value)}
            />
            <button onClick={createMessage}>Send</button>
          </div>
        )}
        {/* Render the Popup component when isPopupOpen is true */}
        {isPopupOpen && <Popup isPopupOpen={isPopupOpen} closePopup={closePopup} />}
      </div>
    </div>
  );
};

export default ChatApp;
